sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    //Type appropriate comment here, and begin script below
    var gx = new GlideAjax('ChangeUtil');
    gx.addParam('sysparm_name', 'startDateValidation');
    gx.addParam('sysparm_value', g_form.getValue('start_date'));

    gx.getXML(callFunction);

    function callFunction(response) {
        var answer = response.responseXML.documentElement.getAttribute("answer");

        if (answer == 3 || answer > 3) {
            //do nothing
        } 
		else {
			 alert('Please select 3 days from current date');
		}

    }


}
