sys_script_include.script:
var ChangeUtil = Class.create();
ChangeUtil.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    startDateValidation: function() {
        var sDate = this.getParameter('sysparm_value');
        var d1 = new GlideDateTime();
        var d2 = new GlideDateTime();
        d2.setValue(sDate);
        var d3 = GlideDate.subtract(d1, d2);
        gs.info(d3.getDisplayValue());
        gs.info(d3.getDayPart());
        return d3.getDayPart();

    },

    type: 'ChangeUtil'
});
